INSERT INTO `exercise_glossary` VALUES (1,'Leggi il testo [R]','Lettura di un testo in cui sono presenti svariate parole con la lettera R. [30 Secondi]','READTEXT',1,'\"Una ragazzina vivace e curiosa, correva nel prato verde rincorrendo un grande farfallone arancione. Attraversò il fiume rumoroso saltando su rocce scivolose, raggiungendo la riva opposta con un sorriso radiante. Respirava l\'aria fresca e sentiva il profumo di rose selvatiche che crescevano lungo il sentiero. Raggiunse un antico castello circondato da torri robuste e mura imponenti. Rimase affascinata dalla sua maestosità e si mise a esplorare ogni corridoio.\"','\"Una ragazzina vivace e curiosa, correva nel prato verde rincorrendo un grande farfallone arancione. Attraversò il fiume rumoroso saltando su rocce scivolose, raggiungendo la riva opposta con un sorriso radiante. Respirava l\'aria fresca e sentiva il profumo di rose selvatiche che crescevano lungo il sentiero. Raggiunse un l\'antico castello circondato da torri robuste e mura imponenti. Rimase affascinata dalla sua maestosità e si mise a esplorare ogni corridoio.\"','Disturbi Motori, Disturbo Fonologico, Disturbo della Voce'),(2,'Scrivi oggetti [R]','Scrittura di oggetti i cui nomi hanno la presenza della lettera R. [2 Minuti]','IMAGESTOTEXT',2,'[\"../images/exercise1/4.png\",\"../images/exercise1/3.png\",\"../images/exercise1/1.png\",\"../images/exercise1/2.png\",\"../images/exercise1/5.png\",\"../images/exercise1/6.png\"]','{\"../images/exercise1/4.png\":\"libro\",\"../images/exercise1/3.png\":\"robot\",\"../images/exercise1/1.png\":\"rana\",\"../images/exercise1/2.png\":\"rosa\",\"../images/exercise1/5.png\":\"orologio\",\"../images/exercise1/6.png\":\"orso\"}','Lesioni Cerebrali Traumatiche, Disturbi Psicologici, Ritardo di Linguaggio'),(3,'Leggi oggetti [R]','Lettura di oggetti i cui nomi hanno la presenza della lettera R. [30 Secondi]','READIMAGES',2,'[\"../images/exercise1/4.png\",\"../images/exercise1/3.png\",\"../images/exercise1/1